// packages/db/src/search.js
function search(query, options = {}) {
  const { limit: limit2 = 20, provider, sessionId, offset = 0 } = options;
  const db3 = getDb();
  const ftsQuery = prepareFtsQuery(query);
  let sql = `
    SELECT
      t.id,
      t.session_id,
      t.turn_number,
      t.user_message,
      t.assistant_response,
      t.model,
      t.ts,
      s.title as session_title,
      s.provider,
      s.cwd,
      highlight(turns_fts, 0, '>>>', '<<<') as user_highlighted,
      highlight(turns_fts, 1, '>>>', '<<<') as assistant_highlighted,
      bm25(turns_fts) as rank
    FROM turns_fts
    JOIN turns t ON turns_fts.rowid = t.rowid
    JOIN sessions s ON t.session_id = s.id
    WHERE turns_fts MATCH ?
  `;
  const params = [ftsQuery];
  if (provider) {
    sql += " AND s.provider = ?";
    params.push(provider);
  }
  if (sessionId) {
    sql += " AND t.session_id = ?";
    params.push(sessionId);
  }
  sql += ` ORDER BY rank LIMIT ? OFFSET ?`;
  params.push(limit2, offset);
  try {
    return db3.prepare(sql).all(...params);
  } catch (err) {
    return searchFallback(query, options);
  }
}
function prepareFtsQuery(query) {
  let cleaned = query.replace(/['"]/g, "").replace(/[()]/g, "").replace(/[-]/g, " ").replace(/[*]/g, "").trim();
  const words = cleaned.split(/\s+/).filter((w2) => w2.length > 0);
  if (words.length === 0) {
    return '""';
  }
  if (words.length === 1) {
    return `"${words[0]}"*`;
  }
  return words.map((w2) => `"${w2}"*`).join(" ");
}
function searchFallback(query, options = {}) {
  const { limit: limit2 = 20, provider, sessionId, offset = 0 } = options;
  const db3 = getDb();
  let sql = `
    SELECT
      t.id,
      t.session_id,
      t.turn_number,
      t.user_message,
      t.assistant_response,
      t.model,
      t.ts,
      s.title as session_title,
      s.provider,
      s.cwd
    FROM turns t
    JOIN sessions s ON t.session_id = s.id
    WHERE (t.user_message LIKE ? OR t.assistant_response LIKE ?)
  `;
  const likeQuery = `%${query}%`;
  const params = [likeQuery, likeQuery];
  if (provider) {
    sql += " AND s.provider = ?";
    params.push(provider);
  }
  if (sessionId) {
    sql += " AND t.session_id = ?";
    params.push(sessionId);
  }
  sql += ` ORDER BY t.ts DESC LIMIT ? OFFSET ?`;
  params.push(limit2, offset);
  return db3.prepare(sql).all(...params);
}

// packages/db/src/stats.js
function getStats() {
  const db3 = getDb();
  const totals = db3.prepare(`
    SELECT
      COUNT(*) as total_sessions,
      SUM(turn_count) as total_turns,
      SUM(total_cost) as total_cost,
      SUM(total_input_tokens) as total_input_tokens,
      SUM(total_output_tokens) as total_output_tokens,
      SUM(total_duration_ms) as total_duration_ms
    FROM sessions
    WHERE status != 'deleted'
  `).get();
  const byProvider = db3.prepare(`
    SELECT
      provider,
      COUNT(*) as sessions,
      SUM(turn_count) as turns,
      SUM(total_cost) as cost,
      SUM(total_input_tokens) as input_tokens,
      SUM(total_output_tokens) as output_tokens
    FROM sessions
    WHERE status != 'deleted'
    GROUP BY provider
    ORDER BY cost DESC
  `).all();
  const byModel = db3.prepare(`
    SELECT
      model,
      COUNT(*) as turns,
      SUM(cost) as cost,
      SUM(input_tokens) as input_tokens,
      SUM(output_tokens) as output_tokens
    FROM turns
    WHERE model IS NOT NULL
    GROUP BY model
    ORDER BY cost DESC
    LIMIT 10
  `).all();
  const recentActivity = db3.prepare(`
    SELECT
      DATE(last_active_at) as date,
      COUNT(*) as sessions,
      SUM(total_cost) as cost,
      SUM(turn_count) as turns
    FROM sessions
    WHERE last_active_at > datetime('now', '-30 days')
      AND status != 'deleted'
    GROUP BY DATE(last_active_at)
    ORDER BY date DESC
  `).all();
  const topSessions = db3.prepare(`
    SELECT
      id,
      title,
      provider,
      turn_count,
      total_cost,
      last_active_at
    FROM sessions
    WHERE status != 'deleted'
    ORDER BY turn_count DESC
    LIMIT 10
  `).all();
  const toolsUsage = getToolsUsage(db3);
  return {
    totalSessions: totals.total_sessions || 0,
    totalTurns: totals.total_turns || 0,
    totalCost: totals.total_cost || 0,
    totalInputTokens: totals.total_input_tokens || 0,
    totalOutputTokens: totals.total_output_tokens || 0,
    totalDurationMs: totals.total_duration_ms || 0,
    byProvider: byProvider.reduce((acc, row) => {
      acc[row.provider] = {
        sessions: row.sessions,
        turns: row.turns || 0,
        cost: row.cost || 0,
        inputTokens: row.input_tokens || 0,
        outputTokens: row.output_tokens || 0
      };
      return acc;
    }, {}),
    byModel,
    recentActivity,
    topSessions,
    toolsUsage
  };
}
function getToolsUsage(db3) {
  if (!db3) db3 = getDb();
  const turns = db3.prepare(`
    SELECT tools_used FROM turns WHERE tools_used IS NOT NULL
  `).all();
  const toolCounts = {};
  for (const turn of turns) {
    try {
      const tools = JSON.parse(turn.tools_used);
      for (const tool of tools) {
        toolCounts[tool] = (toolCounts[tool] || 0) + 1;
      }
    } catch {
    }
  }
  return Object.entries(toolCounts).sort((a2, b2) => b2[1] - a2[1]).slice(0, 20).map(([name, count]) => ({ name, count }));
}

